class Elevator
  attr_reader :floor

  def self.direction(instruction)
    case instruction
    when '('
      1
    when ')'
      -1
    else
      0
    end
  end

  def initialize
    @floor = 0
  end

  def follow(instructions)
    instructions.chars.each do |ch|
      @floor += self.class.direction(ch)
    end
  end
end

